mapper.select:
select r_no rNo, r_date rDate, r_content rContent, r_score rScore, r_sajin rSajin, r_writer rWriter, s_no sNo, d_mno dMno, j_accsation_cnt jAccusationCnt 
from review 
where d_mno=#{dMno}

mapper.update:
update Review
j_accusation_cnt=j_accusation_cnt +1, j_accusation_cnt=0,
where r_no=#{rNo}
